CREATE  FUNCTION `Return_total_funding_records`(fromDate date,toDate date) RETURNS int(11)
BEGIN

RETURN(
select count(*)
from (
select b.CREATED_DATE as Transaction_date, p.MERCHANT_CODE as Parent_Merchant_id,p.BUSINESS_NAME as Parent_Merchant_Name,
b.MERCHANT_ID as SubMerchant_id,b.BUSINESS_NAME as SubMerchat_name,b.TOTAL_AMOUNT as TOTAL_AMOUNT,b.FEE_AMOUNT as FEE_AMOUNT
,b.TXN_AMOUNT as TXN_AMOUNT 
from PG_MERCHANT p 
join 
(
select a.CREATED_DATE as CREATED_DATE,
case pm.MERCHANT_TYPE when 'Merchant' then MERCHANT_CODE else pm.PARENT_MERCHANT_ID end as PARENT_MERCHANT_ID ,
a.MERCHANT_ID as MERCHANT_ID,a.TOTAL_AMOUNT as TOTAL_AMOUNT,a.FEE_AMOUNT as FEE_AMOUNT,a.TXN_AMOUNT as TXN_AMOUNT,
pm.BUSINESS_NAME as BUSINESS_NAME 
from (
select date(CREATED_DATE) as CREATED_DATE,MERCHANT_ID,sum(TOTAL_AMOUNT) as TOTAL_AMOUNT,sum(FEE_AMOUNT) as FEE_AMOUNT, 
sum(TXN_AMOUNT) as TXN_AMOUNT 
from PG_TRANSACTION 
where MERCHANT_SETTLEMENT_STATUS='Executed' 
group by MERCHANT_ID,date(CREATED_DATE)
)a join PG_MERCHANT pm on pm.MERCHANT_CODE=a.MERCHANT_ID 
where pm.MERCHANT_TYPE='SubMerchant'
)b on b.PARENT_MERCHANT_ID=id 
union all 
select a.CREATED_DATE,case pm.MERCHANT_TYPE when 'Merchant' then MERCHANT_CODE else pm.PARENT_MERCHANT_ID end as parentid,
case pm.MERCHANT_TYPE when 'Merchant' then BUSINESS_NAME else pm.BUSINESS_NAME end as parent_name ,
a.MERCHANT_ID,pm.BUSINESS_NAME as MERCHANT_NAME,a.TOTAL_AMOUNT,a.FEE_AMOUNT,a.TXN_AMOUNT 
from (select date(CREATED_DATE) as CREATED_DATE,MERCHANT_ID,sum(TOTAL_AMOUNT) as TOTAL_AMOUNT,
sum(FEE_AMOUNT) as FEE_AMOUNT, sum(TXN_AMOUNT) as TXN_AMOUNT from PG_TRANSACTION 
where MERCHANT_SETTLEMENT_STATUS='Executed'group by MERCHANT_ID,date(CREATED_DATE)
)a join PG_MERCHANT pm on pm.MERCHANT_CODE=a.MERCHANT_ID 
where pm.MERCHANT_TYPE='Merchant'
)x where x.Transaction_date <=date(toDate) 
and x.Transaction_date >= date(fromDate));


 
END ;;
