CREATE  PROCEDURE `MERCHANT_CREATEDBY_UPDATE`()
BEGIN

  DECLARE tempMerchantCreatedBy VARCHAR(20);
  DECLARE tempMerchantConfigId  VARCHAR(20);
  DECLARE term INT DEFAULT 0;
  DECLARE merchant_info1 VARCHAR(50)  ;
  DECLARE merchant_info2 VARCHAR(50)  ;
  
  DECLARE merchant_infos CURSOR FOR
  SELECT U.CREATED_BY,M.MER_CONFIG_ID  FROM PG_MERCHANT_USERS U,PG_MERCHANT M WHERE U.PG_MERCHANT_ID = M.ID;

  DECLARE CONTINUE HANDLER FOR NOT FOUND SET term = 1;
  OPEN merchant_infos;
  
  get_term: LOOP

  FETCH merchant_infos INTO merchant_info1,merchant_info2;
  SET tempMerchantCreatedBy = merchant_info1;
  SET tempMerchantConfigId  = merchant_info2;

  UPDATE PG_MERCHANT
  SET CREATED_BY      = tempMerchantCreatedBy
  WHERE MER_CONFIG_ID = tempMerchantConfigId;
    
  IF term = 1 THEN 
  LEAVE get_term;
  END IF;

  END LOOP get_term; 
  
  CLOSE merchant_infos;

END ;;
