CREATE  PROCEDURE `usp_dailyfunds_report`()
BEGIN

declare v_count int;

SELECT 
  count(*) into v_count
FROM
  (SELECT b.CREATED_DATE AS Transaction_date,
    b.BATCH_ID           AS BATCH_ID,
    p.MERCHANT_CODE      AS Parent_Merchant_id,
    p.BUSINESS_NAME      AS Parent_Merchant_Name,
    b.MERCHANT_ID        AS SubMerchant_id,
