    b.FEE_AMOUNT         AS FEE_AMOUNT ,
    b.TXN_AMOUNT         AS TXN_AMOUNT,
    b.TRANSACTION_ID     as TRANSACTION_ID
  FROM PG_MERCHANT p
  JOIN
    (SELECT a.CREATED_DATE AS CREATED_DATE,
      a.BATCH_ID           AS BATCH_ID,
      pm.LOCAL_CURRENCY    AS LOCAL_CURRENCY,
      CASE pm.MERCHANT_TYPE
        WHEN 'Merchant'
        THEN pm.MERCHANT_CODE
        ELSE pm.PARENT_MERCHANT_ID
      END                AS PARENT_MERCHANT_ID ,
      a.MERCHANT_ID      AS MERCHANT_ID,
      pmb.BANK_ACC_NUM   AS BANK_ACC_NUM,
      pmb.ROUTING_NUMBER AS ROUTING_NUMBER,
      a.TOTAL_AMOUNT     AS TOTAL_AMOUNT,
      a.FEE_AMOUNT       AS FEE_AMOUNT,
      a.TXN_AMOUNT       AS TXN_AMOUNT,
      pm.BUSINESS_NAME   AS BUSINESS_NAME,a.TRANSACTION_ID as TRANSACTION_ID
    FROM
      (SELECT CREATED_DATE AS CREATED_DATE,
        BATCH_ID                 AS BATCH_ID,
        MERCHANT_ID,
        SUM(TOTAL_AMOUNT) AS TOTAL_AMOUNT,
        SUM(FEE_AMOUNT)   AS FEE_AMOUNT,
        SUM(TXN_AMOUNT)   AS TXN_AMOUNT,TRANSACTION_ID as TRANSACTION_ID
      FROM PG_TRANSACTION
      WHERE MERCHANT_SETTLEMENT_STATUS='Executed'
      GROUP BY MERCHANT_ID,
        DATE(CREATED_DATE)
      )a
    JOIN PG_MERCHANT pm
    ON pm.MERCHANT_CODE=a.MERCHANT_ID
    JOIN PG_MERCHANT_BANK pmb
    ON pmb.ID                 =pm.ID
    WHERE pm.MERCHANT_TYPE    ='SubMerchant'
    )b ON b.PARENT_MERCHANT_ID=id
  UNION ALL
  SELECT a.CREATED_DATE,
    a.BATCH_ID,
    CASE pm.MERCHANT_TYPE
      WHEN 'Merchant'
      THEN pm.MERCHANT_CODE
      ELSE pm.PARENT_MERCHANT_ID
    END AS parentid,
    CASE pm.MERCHANT_TYPE
      WHEN 'Merchant'
