    NULL AS SubMerchant,
    NULL AS SubMerchant,
    pm.LOCAL_CURRENCY,
    pmb.BANK_ACC_NUM,
    pmb.ROUTING_NUMBER,
    a.TOTAL_AMOUNT,
    a.FEE_AMOUNT,
    a.TXN_AMOUNT,a.TRANSACTION_ID
  FROM
    (SELECT DATE(CREATED_DATE) AS CREATED_DATE,
      MERCHANT_ID,
      BATCH_ID          AS BATCH_ID,
      SUM(TOTAL_AMOUNT) AS TOTAL_AMOUNT,
      SUM(FEE_AMOUNT)   AS FEE_AMOUNT,
      SUM(TXN_AMOUNT)   AS TXN_AMOUNT,TRANSACTION_ID as TRANSACTION_ID
    FROM PG_TRANSACTION
    WHERE MERCHANT_SETTLEMENT_STATUS='Executed'
    GROUP BY MERCHANT_ID,
      DATE(CREATED_DATE)
    )a
  JOIN PG_MERCHANT pm
  ON pm.MERCHANT_CODE=a.MERCHANT_ID
  JOIN PG_MERCHANT_BANK pmb
  ON pmb.ID             =pm.ID
  WHERE pm.MERCHANT_TYPE='Merchant'
  )x  where x.Transaction_date <=DATE_FORMAT(date(DATE_SUB(sysdate(), INTERVAL 0 day)),'%Y-%m-%d 23:59:59')
  and x.Transaction_date >=  DATE_FORMAT(date(DATE_SUB(sysdate(), INTERVAL 0 day)),'%Y-%m-%d 00:00:00')
  and x.TRANSACTION_ID not in (select ifnull(TRANSACTION_ID,0) from PG_FUNDING_REPORT)
