    )b ON b.PARENT_MERCHANT_ID=id
  UNION ALL
  SELECT a.CREATED_DATE,
    a.BATCH_ID,
    CASE pm.MERCHANT_TYPE
      WHEN 'Merchant'
      THEN pm.MERCHANT_CODE
      ELSE pm.PARENT_MERCHANT_ID
    END AS parentid,
    CASE pm.MERCHANT_TYPE
      WHEN 'Merchant'
      THEN BUSINESS_NAME
      ELSE pm.BUSINESS_NAME
    END  AS parent_name ,
    NULL AS SubMerchant,
    NULL AS SubMerchant,
    pm.LOCAL_CURRENCY,
    pmb.BANK_ACC_NUM,
    pmb.ROUTING_NUMBER,
    a.TOTAL_AMOUNT,
    a.FEE_AMOUNT,
    a.TXN_AMOUNT
  FROM
    (SELECT DATE(CREATED_DATE) AS CREATED_DATE,
      MERCHANT_ID,
      BATCH_ID          AS BATCH_ID,
      SUM(TOTAL_AMOUNT) AS TOTAL_AMOUNT,
      SUM(FEE_AMOUNT)   AS FEE_AMOUNT,
