  ON pm.MERCHANT_CODE=a.MERCHANT_ID
  JOIN PG_MERCHANT_BANK pmb
  ON pmb.ID             =pm.ID
  WHERE pm.MERCHANT_TYPE='Merchant'
  )x  where x.Transaction_date <=DATE_FORMAT(date(DATE_SUB(sysdate(), INTERVAL 1 day)),'%Y-%m-%d 23:59:59')
